const fs = require("fs");

export const Utils = () => {
  // This function handles getting images from the specified website url
  function readDirectory(path) {
    // read the contents of the directory
    fs.readdir(path, (err, files) => {
      if (err) {
        console.error(err);
        return;
      }

      // loop through all files in the directory
      files.forEach((file) => {
        const filePath = `${path}/${file}`;

        // check if the file is a directory
        fs.stat(filePath, (err, stat) => {
          if (err) {
            console.error(err);
            return;
          }

          if (stat.isDirectory()) {
            // if the file is a directory, recursively call this function on it
            readDirectory(filePath);
          } else {
            // if the file is not a directory, read its contents and print them to the console
            fs.readFile(filePath, "utf8", (err, data) => {
              if (err) {
                console.error(err);
                return;
              }

              console.log(`Contents of ${filePath}:`);
              console.log(data);
            });
          }
        });
      });
    });
  }

  return {
    readDirectory,
  };
};
