import { stopWords, text } from "./stopWords.js";

import React, { useState, useEffect } from "react";

const Parser = () => {
  const [tokens, setTokens] = useState({});

  useEffect(() => {
    fetch("/tokens", {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
      },
      body: JSON.stringify(text),
    })
      .then((response) => response)
      .then((data) => {
        console.log(data);
        setTokens(data);
      })
      .catch((error) => {
        console.error(error);
      });
  }, []);

  return (
    <div>
      <h2>
        <br></br>
        Paragraphe :<br></br>
        <br></br>
        {text}
      </h2>

      {tokens.map((token) => (
        <div className="tooken">{token}</div>
      ))}
    </div>
  );
};

export default Parser;
