import { text } from "./text.js";

import React, { useState, useEffect } from "react";

import { dbApi } from "../hooks/dp_api";

const Parser = () => {
  const [tokens, setTokens] = useState({});
  const { sendText } = dbApi();

  useEffect(async () => {
    await sendText(text)
      .then((response) => response.text())
      .then((data) => {
        console.log(data);
        // const object = JSON5.parse(data);
        // console.log(object);
        // setTokens(object);
      })
      .then((response) => response.text())
      .then((data) => {
        console.log(data);
        // const object = JSON5.parse(data);
        // console.log(object);
        // setTokens(object);
      })
      .catch((error) => {
        console.error(error);
      });
  }, []);

  return (
    <div>
      <h2>
        <br></br>
        Paragraphe :<br></br>
        <br></br>
        {text}
      </h2>
      <br></br>
      <br></br>
      <br></br>
      <table
        style={{ width: "500px", margin: "auto" }}
        class="table table-bordered table-striped"
      >
        <thead>
          <tr>
            <th>Mots</th>
            <th>Fr√©quences</th>
          </tr>
        </thead>
        <tbody>
          {tokens &&
            Object.entries(tokens).map(([key, value]) => (
              <tr key={key}>
                <td>{key}</td>
                <td>{value}</td>
              </tr>
            ))}
        </tbody>
      </table>
    </div>
  );
};

export default Parser;
