export const dbApi = () => {
  // This function handles getting images from the specified website url
  const sendText = async (text) => {
    console.log(text);
    fetch("/tokens", {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
      },
      body: JSON.stringify(text),
    })
      .then((response) => response.text())
      .then((data) => {
        return data;
      })
      .catch((error) => {
        console.error(error);
      });
  };

  return {
    sendText,
  };
};
