// const { NlpManager } = require("node-nlp");
const natural = require("natural");

const Parser = () => {
  const text =
    "L'exercice physique est souvent associé à la perte de poids ou à l'amélioration de la condition physique, mais ses avantages pour la santé mentale sont tout aussi importants. Des études ont montré que l'exercice régulier peut aider à réduire le stress, l'anxiété et la dépression. L'exercice augmente la production de neurotransmetteurs comme la dopamine et la sérotonine, qui aident à réguler l'humeur et à réduire les niveaux de stress. De plus, l'exercice peut aider à améliorer la qualité du sommeil, qui est essentielle pour une bonne santé mentale. Il n'est pas nécessaire de s'entraîner intensément pour bénéficier de ces avantages ; une simple marche de 30 minutes par jour peut faire une grande différence. En incorporant de l'exercice dans notre vie quotidienne, nous pouvons améliorer notre santé mentale et notre bien-être général.";
  const stopWords = []; // Ajoutez vos mots-vides ici
  const tokenizerFR = tokenizer.createTokenizer({
    locale: "fr",
  });

  // Fonction pour compter les occurrences des mots dans le texte
  function countWords(text) {
    const results = {};
    tokenizerFR.setEntry(text);
    let token = tokenizerFR.getNextToken();
    while (token) {
      const lemma = token.getValue().toLowerCase();
      if (!stopWords.includes(lemma)) {
        results[lemma] = (results[lemma] || 0) + 1;
      }
      token = tokenizerFR.getNextToken();
    }
    return results;
  }

  // Test de la fonction
  //   const text = "Le chat et le chien sont amis, mais le chat préfère le tapis";
  const wordCounts = countWords(text);
  console.log(wordCounts);

  return (
    <div>
      <h2>
        <br></br>
        Paragraphe :<br></br>
        <br></br>
        {text}
      </h2>
    </div>
  );
};

export default Parser;
