import { stopWords } from "./stopWords.js";

// const { NlpManager } = require("node-nlp");
const natural = require("natural");
const winkPosTagger = require("wink-pos-tagger");
const franc = require("franc");

const tokenizer = new natural.WordTokenizer();
const stemmer = natural.PorterStemmerFr;

const Parser = () => {
  const text = "how are you";

  function countWords(text) {
    const tagger = new winkPosTagger({ language: "french" });

    const results = {};
    const tokens = tokenizer.tokenize(text);
    // console.log(tokens);

    for (let i = 0; i < tokens.length; i++) {
      const token = tokens[i];
      const taggedWords = tagger.tagSentence(token);
      const isVerb = taggedWords[0].pos.startsWith("V");
      if (isVerb) {
        console.log("Verbe => ", token);
      }
      const stem = stemmer.stem(token.toLowerCase());
      if (!stopWords.includes(stem)) {
        results[stem] = (results[stem] || 0) + 1;
      }
    }
    return results;
  }

  // const text = "Le chat et le chien sont amis, mais le chat préfère le tapis";
  const wordCounts = countWords(text);
  //   console.log(wordCounts);

  return (
    <div>
      <h2>
        <br></br>
        Paragraphe :<br></br>
        <br></br>
        {text}
      </h2>
    </div>
  );
};

export default Parser;
