export const dbApi = () => {
  // This function handles getting images from the specified website url
  const sendText = async (titre, text) => {
    return fetch("/tokens", {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
      },
      body: JSON.stringify({ titre, text }),
    })
      .then((resp) => {
        const data = resp.text();
        return data;
      })
      .catch((error) => {
        console.error(error);
      });
  };

  return {
    sendText,
  };
};
